import React, { Component } from "react";

import Aux from "../hoc/Aux/Aux";
import classes from "./Profile.module.css";
import Navbar from "./Navbar";
import Header from "./Header";
import { Waypoint } from "react-waypoint";

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      links: [
        {
          target: "section-home",
          text: "home",
        },
        {
          target: "section-about",
          text: "about",
        },
        {
          target: "section-resume",
          text: "resume",
        },
        {
          target: "section-projects",
          text: "projects",
        },
        {
          target: "section-contact",
          text: "contact",
        },
      ],
      navbar: <Navbar
              links={this.state.links}
              sticky={this.state.stickyNav}
              ></Navbar>,
    };
  }

  stickyNavToggleOn() {
    const newStickyNavState = true;
    this.setState({stickyNav: newStickyNavState}, () => {
      console.log("sticky nav on?", this.state.stickyNav);
    });
  }

  stickyNavToggleOff() {
    const newStickyNavState = false;
    this.setState({stickyNav: newStickyNavState}, () => {
      console.log("sticky nav off?", this.state.stickyNav);
    });
  }
  componentDidUpdate() {
    console.log('Component did update state');
  }
  render() {
    let navbar = <Navbar
        links={this.state.links}
        sticky={this.state.stickyNav}
        ></Navbar>;
    return (
      <Aux>
        {navbar}
        <Waypoint
          onEnter={this.stickyNavToggleOff}
          onLeave={this.stickyNavToggleOn}>
            <div>
              <Header></Header>
            </div>
        </Waypoint>

        <section id="section-about" className={classes.aboutSection}>
          <div className={classes.aboutBox}></div>
        </section>
        <section id="section-resume">Resume</section>
        <section id="section-projects">Projects</section>
        <section id="section-contact">Contact</section>
      </Aux>
    );
  }
}

export default Profile;
