import React, { Component } from 'react';

import Aux from '../hoc/Aux/Aux';
import Navbar from './Navbar';
import { Link, animateScroll as scroll } from "react-scroll";
import classes from './Layout.module.css';

class Layout extends Component {
    constructor(props) {
        super(props);
        this.state = {
            links: [
                {
                    target: 'section-home',
                    text: 'home',
                },
                {
                    target: 'section-about',
                    text: 'about',
                },
                {
                    target: 'section-resume',
                    text: 'resume',
                },
                {
                    target: 'section-projects',
                    text: 'projects',
                },
                {
                    target: 'section-contact',
                    text: 'contact',
                }
            ]
        }
    }
    render() {
        return(
            <Aux>
                <section id="section-home">
                <header className={classes.Header}>
                    <Navbar className={classes.Navbar} navListStyle={classes.navItems} navItemStyle={classes.navItem} links={this.state.links}></Navbar>
                    <div className={classes.Home_box}>
                        <div className={classes.row}>
                            <h1>Aaron Lewis</h1>
                            <h2>Aspiring Full-Stack Software Developer</h2>
                            <h3>Background Image: Valdez-Cordova, Alaska by Nikomi Fong</h3>
                        </div>
                        <hr style={{backgroundColor: "#4afdf5"}} className={`${classes.row} ${classes.divider}`} />
                        <div className={classes.row}>
                            <ul className={classes.Social}>
                                <li>
                                    <a href="https://github.com/Alewis91" target="_blank" rel="noopener noreferrer"><ion-icon name="logo-github"></ion-icon></a>
                                </li>
                                <li>
                                    <a href="https://www.linkedin.com/in/alewis91/" target="_blank" rel="noopener noreferrer"><ion-icon name="logo-linkedin"></ion-icon></a>
                                </li>
                                <li>
                                    <a href="mailto:aaron.lewis.swe@gmail.com">
                                        <ion-icon className={classes.large_icon} name="mail"></ion-icon></a>
                                </li>
                            </ul>
                        </div>                    
                        <div className={`${classes.row} ${classes.bottom_nav_button}`}>
                            <Link
                                activeClass="active"
                                to={el.target}
                                spy={true}
                                smooth={true}
                                offset={0}
                                duration={500}>
                                    <ion-icon size="64px" 
                                    className={classes.large_icon} 
                                    name="caret-down-circle"></ion-icon>               
                            </Link>
                        </div>    
                    </div>
                </header>    
                </section>

                <section id="section-about" style={{height: "1000px"}}>
                    About
                </section>
                <section id="section-resume" style={{height: "1000px"}}>
                    Resume
                </section>
                <section id="section-projects" style={{height: "1000px"}}>
                    Projects
                </section>
                <section id="section-contact" style={{height: "1000px"}}>
                    Contact
                </section>
            </Aux>
        );
    }
}

export default Layout;
